<script>
import DashCard from '@/common/DashCard.vue'
import ProjectRow from './ProjectRow.vue'
import request from '@/services/axios_instance'
import { format } from 'date-fns'

export default {
  name: 'ProjectOverviewCard',
  components: {
    DashCard,
    ProjectRow
  },

  props: {
    id: [Number, String],
    dashboard: Boolean,
    viewMoreLink: Object
  },

  data: () => ({
    projects: [],
    loading: false
  }),

  created() {
    this.loading = true
    request
      .get('api/projects/' + this.id)
      .then(({ data }) => this.projects.push(data))
      .finally(() => (this.loading = false))
  },

  methods: {
    format_date(date) {
      return format(date, 'MMMM D, YYYY')
    },
    format_time(time) {
      let [hours, mins, secs] = time.split(':')
      return `${hours} HRS | ${mins} MINS | ${secs} SECS`
    },
    filterText(text) {
      let cleanText = text.replace(/(<([^>]+)>)/gi, '')
      return cleanText.length > 40 ? cleanText.slice(0, 39) + '...' : cleanText
    }
  }
}
</script>

